angular.module("il.ui.advancedCombo", ['ngSanitize','pascalprecht.translate','ui.bootstrap','il.ui.table','il.ui.modal'])
	.directive('ilAdvancedCombo', function() {
		var controller = ['$scope','$timeout','$attrs', function ($scope,$timeout,$attrs) {
			$scope.onSelectItem=function(item){
				$scope.model=item;
				$scope.show=false;
			}
		}];
		
		template='<form class="form-inline"> <div class="form-group"> <div class="input-group"> <div class="input-group-addon" style="padding: 0px"> <button class="btn" style="padding: 0px 1em 0px 1em" ng-click="show=true"><span class="glyphicon glyphicon-th-list"></span></button> </div> <input type="text" class="form-control" id="exampleInputAmount" placeholder="Select one" disabled="true" ng-model="model[field]" style="background-color: white"> </div> </div></form><il-modal show="show" title="title" footer-visible="false" size="size"> <table il-table search="true" model="list" columns="columns" class="table table-striped" items-per-page="itemsPerPage||5" on-click-item="onSelectItem(item)" ></table></il-modal>';
		
		return {
			restrict: 'E',
			scope: {
	              z:'=',
	              title:'=',
	              columns:'=',
	              list:'=',
	              model:'&',
	              field:'=',
	              itemsPerPage:"=",
	              size:'=',
			},
			controller: controller,
			template:template
		};
	});