angular.module("il.ui.messageBox", ['ngSanitize','pascalprecht.translate','ui.bootstrap'])
	.directive('ilMessageBox', function() {
		var controller = ['$scope','$timeout','$attrs', function ($scope,$timeout,$attrs) {
			$scope.byDefault=function(field,value){
				if ($scope[field]==undefined)
					$scope[field]=value;
			}
			
			$scope.byDefault("type","message");
			
			if ($scope.type=="loading"){
				$scope.byDefault("titleVisible",false);
			}else{
				$scope.byDefault("titleVisible",true);
			}
			
			//Prepare zindex
			if ($scope.z==undefined)
				$scope._zIndex=100;
			else
				$scope._zIndex=$scope.z*100;
			
				
			$scope._zIndexBg=$scope._zIndex-1;
			
			//Prepare functions
			
			
			
			
			$scope.close=function(success){
				if ($scope.autoClose==undefined||$scope.autoClose)
					$scope.show=false;
				
				if (success){
					if ($scope.onSuccess!=undefined)
						$scope.onSuccess({});
				}else{
					if ($scope.onCancel!=undefined)
						$scope.onCancel({});
				}
			}
		}];
		
		template='<div style="position: fixed; top:0; left:0; right: 0; bottom: 0; background-color: rgba(0,0,0,0.5); z-index: {{_zIndexBg}}" ng-if="show"></div><div style="z-index: {{_zIndex}}; position: fixed; left: 0px; right: 0px;" ng-if="show"> <div style="margin: 0 auto; background-color: white"> <div class="modal" style="display: block"> <div class="modal-dialog" style="width: {{size||\'600px\'}}; top:{{top||\'20px\'}};"> <div class="modal-content"> <div class="modal-header" ng-if="titleVisible"> <button type="button" class="close" ng-click="close(false)" ng-show="closeVisible"><span aria-hidden="true">&times;</span></button> <h4 class="modal-title">{{title}}</h4> </div> <div class="modal-body"> <div ng-if="type==\'loading\'" style="text-align: center"> <p><i class="fa fa-spinner fa-pulse fa-5x"></i></p> <p>Please wait...</p> </div> </div> <!-- <div class="modal-footer" ng-if="trueIfUndefined(footerVisible)"> <button type="button" class="btn btn-default" ng-click="close(false)" ng-if="trueIfUndefined(cancelVisible)">{{cancelLabel||\'Cancel\'}}</button> <button type="button" class="btn btn-primary" ng-if="trueIfUndefined(successVisible)" ng-click="close(true)">{{successLabel||\'Save changes\'}}</button> </div> --> </div> </div><!-- /.modal-dialog --> </div><!-- /.modal --> </div></div>';
		
		return {
			restrict: 'E',
			scope: {
	              show: '=?',
	              z:'=',
	              title:'=',
	              type:'=',
	              
	              cancelVisible:'=',
	              onCancel:'&',
	              cancelLabel:'=',
	              
	              successVisible:'=',
	              onSuccess:'&',
	              successLabel:'=',
	              
	              footerVisible:'=',
	              closeVisible:'=',
	              titleVisible:'=',
	              
	              size:'=',
	              
	              autoClose:'=',
	              
	              top:'=',
	              
			},
			controller: controller,
			template:template,
		};
	});