function ilUpload_basic(url,config){
	this._isBasic=true,
	this.url=url;
	this.show=false;
	this.uploading=false;
	this.error=false;
	this.percent="0%";
	
	this.pick=function(successCallback,errorCallback){
		if (!this.$scope.useUpload)
			alert("You must add ng-file-upload javascript library");
			
		this.show=true;
		this.successCallback=successCallback;
		this.errorCallback=errorCallback;
	}
	
	this.upload = function(files) {
		this.uploading=true;
		system=this;
		
		files.forEach(function(file){
			system.Upload.upload({
				url: system.url,
				file: file
			}).then(function(resp) {// file is uploaded successfully
				system.show=false;
				resp.data.filename=resp.data.name;
				system.successCallback(resp.data);
				
			}, function(resp) { // handle error
				system.error=true;
				system.uploading=false;
				system.errorCallback("");
			}, function(evt) {// progress notify
				system.percent=""+parseInt(100.0 * evt.loaded / evt.total)+ '%';
			});
		});
	    
	}
}

useUpload=false;
moduleList=['ngSanitize','pascalprecht.translate','ui.bootstrap'];
try { 
	angular.module("ngFileUpload") 
	moduleList.push('ngFileUpload'); 
	moduleList.push('il.ui.modal')
	useUpload=true;
} catch(err) {}

angular.module("il.ui.upload2", moduleList)
	.directive('ilUpload2', function() {
		var controller = ['$scope','$timeout','$attrs'];
		
		if (useUpload)
			controller.push("Upload");
		
		controller.push(function ($scope,$timeout,$attrs,Upload) {
			$scope.useUpload=useUpload;
			
			$scope.defaultValue=function(varName,value){
				if ($scope[varName]==undefined)
					$scope[varName]=value;
			}
			
			$scope.defaultValue("multiple",false);
			$scope.defaultValue("slim",false);
			
			$scope.system.$scope=$scope;
			$scope.system.Upload=Upload;
			
			if (!Array.isArray($scope.model[$scope.field]))
				$scope.model[$scope.field]=[];
							
			$scope.getFiles=function(){
				return $scope.model[$scope.field];
			}
						
			$scope.getSize=function(file){
				if (file.size/(1024*1024)>=1)
					return "" + Math.round(100*file.size/(1024*1024))/100 + " Mb.";
				else if (file.size/(1024)>=1)
					return "" + Math.round(file.size/1024) + " Kb.";
				else
					return "" + file.size + " bytes";
			}
			
			$scope.isImage=function(file){
				switch(file.mimetype){
					case "image/png":
					case "image/jpg":
					case "image/jpeg":
					case "image/gif":
						return true;
					break;
					default:
						return false;
				}
			}
			
			$scope.getMimeAwesomeClass=function(file){
				switch($scope.getExtension(file)){
					case "zip":
					case "rar":
					case "tar":
					case "tgz":
						return "fa-file-zip-o";
					break;	
					case "doc":
					case "docx":
						return "fa-file-word-o";
					break;
					case "xls":
					case "xlsx":
						return "fa-file-excel-o";
					break;
					case "txt":
						return "fa-file-text-o";
					break;
					case "pdf":
					case "pdfx":
						return "fa-file-pdf-o"
					break;
					case "mp3":
						return "fa-file-audio-o";
					break;
					case "mp4":
					case "avi":
					case "mov":
					case "mpg":
						return "fa-file-video-o";
					break;
					case "tiff":
					case "bmp":
						return "fa-file-picture-o";
					break;
					default:
						return "fa-file-o";
				}
			}
			
			$scope.getExtension=function(file){
				v=file.filename.split(".")
				return v[v.length-1];
			}
			
			$scope.getMime=function(file){
				switch($scope.getExtension(file)){
					case "zip":
					case "rar":
					case "tar":
					case "tgz":
						return "Compressed file";
					break;	
					case "doc":
					case "docx":
						return "Word";
					break;
					case "xls":
					case "xlsx":
						return "Excel";
					break;
					case "txt":
						return "Text file";
					break;
					case "pdf":
					case "pdfx":
						return "PDF"
					break;
					case "mp3":
						return "audio";
					break;
					case "mp4":
					case "avi":
					case "mov":
					case "mpg":
						return "video";
					break;
					case "tiff":
					case "bmp":
						return "image";
					break;
					default:
						return "file";
				}
			}
			
			
			$scope.pick=function(){
				$scope.system.pick(
					function(file){
						if (!$scope.multiple)
							$scope.model[$scope.field]=[];
							
						$timeout(function(){
							$scope.model[$scope.field].push(file);
							
							if ($scope.onChange!=undefined)
								$scope.onChange({list:$scope.getFiles()});
						})
						
						
					},
					function(errorText){
						if (console) console.debug("Error",errorText);
					}
				);
			}
			
			$scope.removeFile=function(file){
				if (confirm("Are you sure?")){
					list=$scope.getFiles();
					for (i=0; i<list.length; i++)
						if (list[i]==file)
							list.splice(i,1);
				
					if ($scope.onChange!=undefined)
						$scope.onChange($scope.getFiles());
				}
			}
		
		});
		
		template='<div ng-if="!slim || multiple"> <table class="table"> <tr ng-if="model[field].length>0 && userFields"> <td colspan="3"></td> <th ng-repeat="field in userFields" style="text-align: center"> {{field.label}} </th> <td></td> </tr> <tr ng-repeat="file in model[field]"> <td style="vertical-align: middle; text-align: center"> <img ng-if="isImage(file)" ng-src="{{file.url}}" style="max-height: 3em; max-width: 10em"> <span ng-if="!isImage(file)"><i class="fa" ng-class="getMimeAwesomeClass(file)" style="margin-right: 0.5em"></i> {{getMime(file)}}</span> </td> <td style="vertical-align: middle"> <a href="{{file.url}}"> {{file.filename}} </a> </td> <td style="vertical-align: middle"> {{getSize(file)}} </td> <td ng-repeat="field in userFields" style="vertical-align: middle; text-align: center"> <select ng-if="field.type==\'combobox\'" ng-model="file[\'user_\'+field.id]" ng-options="op.value as op.label for op in field.options"></select> <textarea ng-if="field.type==\'text\'" ng-model="file[\'user_\'+field.id]" style="width: 100%"></textarea> <input type="checkbox" ng-if="field.type==\'checkbox\'" ng-model="file[\'user_\'+field.id]"> </td> <td style="vertical-align: middle"> <span class="glyphicon glyphicon-trash" style="cursor:pointer" ng-click="removeFile(file)"></span> </td> </tr> </table> <div style="width: 100%; text-align: center"> <p ng-if="model[field].length==0">{{\'No files\'|translate}}</p> <button type="button" class="btn btn-primary" ng-click="pick()">{{\'Select file\'|translate}}</button> </div></div><div ng-if="slim && !multiple"> <table style="width: 100%"> <tr> <td> <div> <a href="{{model[field][0].url}}"> {{model[field][0].filename}} </a> </div> </td> <td> <button type="button" class="btn btn-primary" ng-click="pick()">{{\'Select file\'|translate}}</button> </td> </tr> </table></div><il-modal ng-if="useUpload && system._isBasic && system.show" show="system.show" title="\'Upload a file\'" auto-close="true" footer-visible="false" close-visible="!system.uploading"> <div style="text-align: center; height: 200px"> <div ng-show="!system.uploading && !system.error" ngf-drop ng-model="files" style="width: 100%; height: 200px; background-color: #F9F9F9; border: 3px dotted silver" ngf-change="system.upload($files)"> <div style="position: relative; top:65px"> <button class="btn btn-primary" ngf-select ng-model="system.files" ngf-change="system.upload($files)">Select a file</button> <p style="font-size: 140%; color: silver; margin-top: 10px">or drop here a file</p> </div> </div> <div ng-show="system.uploading" style="padding-top: 50px"> <span >Uploading...</span> <div style="margin:0 auto; padding: 1px; border: 1px solid silver; width: 80%;"> <div style="width: 0%;height: 18px;background: #286090;" ng-style="{ width: system.percent }"></div> </div> </div> <div ng-show="system.error" style="padding-top: 50px"> <p style="color:red">Error uploading file</p> <button class="btn btn-primary" ng-click="system.error=false">Retry</button> </div> </div></il-modal>';
		
		return {
			restrict: 'E',
			scope: {
				system:"=",
				model:'=',
				field:"=",
				multiple:"=?",
				userFields:"=?",
				slim:"=?",
				onChange:"&",			  
			},
			controller: controller,
			template:template
		};
	});