angular.module("il.ui.yearCalendar", ['ngSanitize','pascalprecht.translate'])
	.directive('ilYearCalendar', function() {
		var controller = ['$scope','$timeout','$attrs', function ($scope,$timeout,$attrs) {
			if ($scope.dayNames==undefined)
				$scope.dayNames=["L","M","X","J","V","S","D"];
				
			console.debug("In");
			
		}];
		
		template='<table class="table"> <tr> <th></th> <th ng-repeat="day in dayNames">{{day}}</th> <th ng-repeat="day in dayNames">{{day}}</th> <th ng-repeat="day in dayNames">{{day}}</th> <th ng-repeat="day in dayNames">{{day}}</th> <th ng-repeat="day in dayNames">{{day}}</th> <th ng-repeat="day in dayNames">{{day}}</th> </tr> <</table>';
		
		return {
			restrict: 'E',
			scope: {
	              dayNames: "=?",
	              enabledDays: '=?',
	              year:"=?",
	              
			},
			controller: controller,
			template:template
		};
	});